mapper.select:
SELECT bulletin_no ,title ,content ,writer ,attached_file ,inquiry_date ,answer ,inquiry_type ,inquiry_state FROM inquiry WHERE inquiry_type = #{inquiryType}
AND title = #{title}
ORDER BY bulletin_no DESC

mapper.insert:

	   SELECT NVL(MAX(bulletin_no), 0 ) + 1
	   FROM   news

INSERT INTO news
	(
		bulletin_no
		,title
		,content
		,writer
		,attached_file
		,inquiry_date
		,answer
		,inquiry_type
		,inquiry_state
   )
 VALUES
   (
   		#{bulletinNo}
   		,#{title}
   		,#{content}
   		,#{writer}
   		,to_char(#{attachedFile})


   			,#{inquiryDate}


   	  		,sysdate


   		,#{inquiryType}
   		,#{inquiryState}
   	)

mapper.update:
UPDATE news
 SET   answer = #{answer}
WHERE bulletin_no = #{bulletinNo}
