mapper.select:
SELECT bulletin_no ,title ,content ,writer ,writing_date ,attached_file ,bulletin_type FROM news
OR title = #{bulletinNo}
ORDER BY title

mapper.insert:

	   SELECT NVL(MAX(bulletin_no), 0 ) + 1
	   FROM   news

INSERT INTO news
	(
		bulletin_no
		,title
		,content
		,writer
		,writing_date
		,attached_file
		,bulletin_type
   )
 VALUES
   (
   		#{bulletinNo}
   		,#{title}
   		,#{content}
   		,#{writer}


   			,#{writingDate}


   	  		,sysdate


   		,#{attachedFile}
   		,#{bulletinType}
   	)

mapper.update:
UPDATE news
 SET
	title = #{title}
   ,content = #{content}
   ,writer = #{writer}
   ,writing_date = sysdate
   ,attached_file = #{attachedFile}
WHERE bulletin_no = #{bulletinNo}
