mapper.insert:
insert into payment( payment_code
					, sender
					, recipient
					, price
					, order_name
					, method
					, requested_at
					, status)
values	( #{paymentCode}
		, #{sender}
		, #{recipient}
		, #{price}
		, #{orderName}
		, #{method}
		, sysdate
		, #{status} )

insert into add_vbank ( payment_code
						, bank_code
						, bank_name
						, bank_account
						, bank_username
						, expired_at )
values( #{paymentCode}
		, #{bankCode}
		, #{bankName}
		, #{bankAccount}
		, #{bankUsername}
		, #{expiredAt} )
