mapper.insert:

	   SELECT NVL(MAX(comment_code), 0 ) + 1
	   FROM   pet_stargram_comment

INSERT INTO pet_stargram_comment
	(
		 comment_code
		,writer
		,content
		,created_date
		,bulletin_no
   )
 VALUES
   (
   		 #{commentCode}
   		,#{writer}
   		,#{content}
   		,NVL(#{createDate}, sysdate)
   		,#{bulletinNo}
   	)
