mapper.insert:

			select  nvl(max(to_number(substr(puppy_code, -6), 'xxxxxx')), 0) + 1
		    from    puppy
		    where   substr(puppy_code, 1, 7) = 'D' || to_char(sysdate, 'yyMMdd')

		INSERT INTO puppy
		(
		    puppy_code
		    , dog_name
		    , dog_breed
		    , age
		    , gender
		    , dog_size
		    , weight
		    , personality
		    , neutralization_pre_abs
		    , disease_pre_abs
		    , note
		    , client_user_Id
		    , picture

		)
		VALUES 
		(

			'D' || to_char(sysdate, 'yyMMdd') || LPAD(LTRIM(to_char(#{puppyCode}, 'xxxxxx')), 6, '0')
		    , #{dogName}
		    , #{dogBreed}
		    , #{age}
		    , #{gender}
		    , #{dogSize}
		    , #{weight}
		    , #{personality}
		    , #{neutralizationPreAbs}
		    , #{diseasePreAbs}
 			, #{note}
 			, #{clientUserId}
 			, #{picture}

		)

mapper.update:
UPDATE puppy
SET    dog_name = #{dogName}
	 , picture = #{picture}	
	 , age = #{age}
	 , dog_breed = #{dogBreed}
	 , dog_size = #{dogSize}
	 , weight = #{weight}
	 , personality = #{personality}
	 , note = #{note}
WHERE   client_user_Id = #{clientUserId}
AND     puppy_code = #{puppyCode}
