mapper.insert:

			SELECT NVL(MAX(TO_NUMBER(puppy_code)), 0) + 1 as "puppy_code"
			FROM   puppy

		INSERT INTO puppy
		(
		    puppy_code
		    , dog_name
		    , dog_breed
		    , age
		    , gender
		    , dog_size
		    , weight
		    , personality
		    , neutralization_pre_abs
		    , disease_pre_abs
		    , note
		    , client_user_Id
		    , picture

		)
		VALUES 
		(

			  LPAD(#{puppyCode},10, '0')
		    , #{dogName}
		    , #{dogBreed}
		    , #{age}
		    , #{gender}
		    , #{dogSize}
		    , #{weight}
		    , #{personality}
		    , #{neutralizationPreAbs}
		    , #{diseasePreAbs}
 			, #{note}
 			, #{clientUserId}
 			, #{picture}

		)

mapper.update:
UPDATE puppy
SET    dog_name = #{dogName}
	 , picture = #{picture}	
	 , age = #{age}
	 , dog_breed = #{dogBreed}
	 , dog_size = #{dogSize}
	 , weight = #{weight}
	 , personality = #{personality}
	 , note = #{note}
WHERE   client_user_Id = #{clientUserId}
AND     puppy_code = #{puppyCode}
